import React from 'react';
import Header from './Header';
import Navigation from './Navigation';

const News = () => {
  const newsItems = [
    {
      id: 1,
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuAIr5fvH2zpeaJctE6MaKoBQ5IGYi6SP5mlsticCto1ujnMWWBQckaVcU7UkSKthYX1YET8c-3E7dCDnW1lH2gRBt1PkkVoiP2dBRZDARHCOqPtpGQJ4mtxlwm2wECqg7XLM-gMLl91UEx7qDj9zBaPbgcU0wf6Lfg0iNQpaIPvE33Q_fg4B_k6wdf6ReOs9qm1FuCrAZObrGZgnPFnmnV-21sLTQ8obuk3of5-EtGy-Z4QPn1sqR0tjB2F84UUWQDigDKdjyFJg_Ew',
      title: 'Tech stocks surge as market optimism grows',
      sentiment: 'positive',
      emoji: 'ðŸš€'
    },
    {
      id: 2,
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuDNjuGswIeWzNsegqLgheYyGuNr_4Dnawfrt4kdoAsHF9aom-bE2V1OrkROquNk1dp1O4zINuJCelKrsk9OM6IvhJXb5kwZ6TlpLRvY61dTLPPkV5ew5p3chNbM48n5QTG6ieFoUi1GOeI9UCAwj_39zxx87PwyIZajG6-1EzOzAInBdiBjIEZuVr3AyFo_Ya9hAuFy6EbfZQOAsvrOEj_8-kKFWW4l1dORachYj-KpAFQnJKvzRmLWkGbGX0Ed95W_qpG1qoz-IZeG',
      title: 'Federal Reserve holds steady on interest rates',
      sentiment: 'neutral',
      emoji: 'ðŸ“°'
    },
    {
      id: 3,
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuAIoCAqFHNROP1K4_q5OxPndagAnv_Z4NjzOFCQ2egS6yNP7Jaiv5vrlhbkYXZBsz_m83Ir-OCXrPBUp4QIl2KOOeOg4Rs8wBS4SJAI9_FA3gEfzfzkqH9f1F_7Ks3Mzr257r2z14p34o7IQYIV88Ite4h2M4f7cRFiD49foROy0x_gfK2ODMSzidHLLRzy4bEWS9ScTr1MvISnD_bQcInB6HStbAt-GiTjMugoUgsXb_qws4GAIZ0ULToVFq1d-6GP3pH9-YgbNEFt',
      title: 'Oil prices drop amid global supply concerns',
      sentiment: 'negative',
      emoji: 'âš ï¸'
    },
    {
      id: 4,
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuA2B3SSTuKDiDUFFxnQBSQn4J5VbEOgXiCRdqRgMFhTd7UOLaXvHYZJBi-vW1-NHrVwUansI0LfTCiFXV8U-6UuziZsvUzd-OtMLEZCCg2EFDkIHedybqBNq62wYUfHPLaw9RVQY7d33-DMtgKQhis1iHrP4MP0C447D2TiaVKxSTAwVXkmYNahr0n4-YLnDVmsg2GTOUoSaIdH38VEt8Fraz-HBUgOHCd1T5UDehkM9tgJA6xzf5_R-RlMY6IvPbUU6ei-HWhPh1L5',
      title: 'Renewable energy sector sees record investment',
      sentiment: 'positive',
      emoji: 'ðŸš€'
    },
    {
      id: 5,
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuACyUILuYYavleR1vB0GMnQoSG0HbpAQZVwlK4GH4VGy2rWHgx2qbx_5uo2oqVe1hVKcNZijZlU6blvq1KhmML4GTth4N5k6MgBQpYBC9O29mST-EAIs26yA0rULslqMQdv_gxt92u5xJIXC4H8iHgBvR0jLAFxhOpq3hy_kSaIg3iicF8KsVgapXiTrvho9BH7EAE5xjSSfFuaQ2mR8FsnHL4bw8uYLd2i5TSC-c4dOBNkBC7xGfhYELRE9T0WBGaw9Gd2j3viH_EG',
      title: 'Consumer spending remains stable in Q2',
      sentiment: 'neutral',
      emoji: 'ðŸ“°'
    },
    {
      id: 6,
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuDokHER5kjCFbslC0EnHGcgh61gyYlfGfu3KRqbJ81A9I3pTuqJ1D0PxPNP-pN44uxqr90kGvDJTmtwz6gFnGa2BlWBah88Ih9Go7eTpBjv9BH0NGLHE5H-dlkXVqYzbXEk3OQoJgFmIgT8DnUn1vV38eXnGVnqNUWGs6NxJ6kMHZXTTVmr_r-Gyq3Fe13afYCB0ujo_YwL2AllPeEamjVDt8RAX3jPh6moZ8yCWx9uf95Lwd2m9XNBJ1f5Ptx0FOVCTRtf8atTjNdU',
      title: 'Inflation fears persist despite economic growth',
      sentiment: 'negative',
      emoji: 'âš ï¸'
    }
  ];

  const getSentimentColor = (sentiment) => {
    switch (sentiment) {
      case 'positive':
        return 'text-green-500';
      case 'negative':
        return 'text-amber-500';
      case 'neutral':
      default:
        return 'text-slate-500 dark:text-slate-400';
    }
  };

  return (
    <div className="relative flex h-auto min-h-screen w-full flex-col justify-between group/design-root overflow-x-hidden">
      <div className="flex-grow">
        <Header title="News" />
        <main className="divide-y divide-slate-200 dark:divide-slate-800">
          {newsItems.map((item) => (
            <div key={item.id} className="flex items-start gap-4 p-4">
              <img
                alt="News Image"
                className="size-16 rounded-lg object-cover"
                src={item.image}
              />
              <div className="flex-1">
                <p className="text-base font-medium text-slate-800 dark:text-slate-200">
                  {item.title}
                </p>
                <div className="mt-1 flex items-center gap-2">
                  <span className="text-lg">{item.emoji}</span>
                  <span className={`text-sm font-medium ${getSentimentColor(item.sentiment)}`}>
                    {item.sentiment.charAt(0).toUpperCase() + item.sentiment.slice(1)}
                  </span>
                </div>
              </div>
            </div>
          ))}
        </main>
      </div>
      <Navigation />
    </div>
  );
};

export default News;
